DROP TABLE IF EXISTS `bi_pc310`;
CREATE TABLE `bi_pc310` (
  `id` int(8) unsigned NOT NULL AUTO_INCREMENT,
  `game_name` varchar(20) NOT NULL,
  `guest` varchar(20),
  `main` varchar(20),
  `game_time` varchar(12) NOT NULL,
  `game_result` varchar(1),
  `all_down` int(6) unsigned,
  `all_eggs` int(15) unsigned,
  `open_people` int(5) unsigned,
  `result_mode` int(1) unsigned,
  `result_0` int(10) unsigned,
  `result_1` int(10) unsigned,
  `result_2` int(10) unsigned,
  `game_desc` varchar(300),
  `CTIME` int(11) unsigned NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;

